const prompt = require('prompt-sync')();

let matriz = []
let linhas = 2
let colunas = 4
let contador = [0,0]
let soma_pares = 0
for (let i = 0; i < linhas; i++) {
    matriz [i] = []
    for (let j = 0; j < colunas; j++) {
        let numero = prompt(`Insira o numero na posição [${i}][${j}]:`)
        matriz[i][j] = Number(numero) 
        if (matriz [i][j] >= 12 && matriz[i][j] <= 20)
        {
            contador[0]++
        }
        if (matriz[i][j] % 2 == 0){
            soma_pares = soma_pares + matriz[i][j]
            contador[1]++
        }
    }
}
console.log(matriz)
console.log(`Quantidade de números entre 12 e 20 = ${contador[0]}`)
if(contador [1] > 0)
{
    console.log(`Média dos números pares da matriz = ${soma_pares/contador[1]}`)
}