const prompt = require('prompt-sync')();

let matriz = [
    [2,3,4],[1,2,4],[90,18,13],
    [12,33,44],[55,71,12],[9,100,33],
    [21,224,66],[12,55,77],[7,8,15]
]
let posicoes = []
let maior = matriz[0][0], menor = matriz[0][0]
for (let i = 0; i < 6; i++ ) {
    for (let j = 0; j < 6; j ++)
    {
        if (matriz[i][j] > maior)
        {
            maior = matriz[i][j]
            posicoes[0] = i
            posicoes[1] = j
        }
        if (matriz[i][j] < menor)
        {
            menor = matriz[i][j]
            posicoes[2] = i
            posicoes[3] = j
        }
    }
}
console.log(`Maior elemento ${maior} e menor elemento ${menor}`)
console.log(`Nas posições ${posicoes[0]} e ${posicoes[1]}`)
console.log(`Nas posições ${posicoes[2]} e ${posicoes[3]}`)