const prompt = require('prompt-sync')();

let matriz = []
let vetor = []
let soma_alu = 0 
let media_alu = 0
for (i = 0; i < 5; i ++)
{
    vetor.push(prompt(`Informe o nome do aluno `))
    matriz[i] = []
    for (j = 0; j < 4;j ++)
    {
        matriz[i][j] = (Math.random()* 10).toFixed(2)
    }
}
for (i = 0; i < 5; i ++)
{
    soma_alu = 0
    for (j = 0; j < 4; j ++)
    {
        soma_alu += Number (matriz[i][j])
    }
    media_alu = soma_alu/matriz[i].length
    console.log(`Aluno = ${vetor[i]} tem a mÃ©dia de ${(media_alu).toFixed(2)}`)
    if (media_alu >= 6)
    {
        console.log(`Aluno = ${vetor[i]} foi aprovado`)
    }
    else if(media_alu < 6)
    {
        console.log(`Aluno = ${vetor[i]} em exame`)
    }
}